import React from 'react';
import { connect } from 'react-redux';

class MainPage extends React.Component {
    render() {
        return (
                <div className="page main">
                    <div className="main-title" 
                    style={this.props.hover ? {marginTop: '20rem'} : null}>Zakopane Accomodation</div>
                    <div className="main-description">
                        You also thing that our mountains are beautiful and want to spend an amazing vacations 
                        in Zakopane?
                        <p style={{ marginTop: '1rem' }}> 
                            Check our wide range of houses and rooms.
                        </p>
                    </div>
                    <ion-icon name="arrow-down"></ion-icon>
                </div>
        );
    };
};

const mapStateToProps = state => {
    return { hover: state.headerState };
}
/*<div className="subtitle">
                Situated in the breathtakingly beautiful Zakopane. 10 min on foot from the nearest mountain trail 
                and if that is also too long for you, we can also rent you a bike or even a car if you plan on 
                going a little futher.
                </div>*/
export default connect(mapStateToProps)(MainPage);
