const STATE_INITIAL = {
    hoverShouldShow: true,
    changeStyle
};

export default (state = STATE_INITIAL, action) => {
    switch(action.type) {
        case 'HOVER_SHOULD_SHOW': 
            return action.payload;
        default:
            return state;
    };
};
