import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { hoverNav } from './actions';

class NavBar extends React.Component {
    state = { headerHover: false, visible: true };

    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
    };
    
    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
    };

    componentDidUpdate = () => !this.props.hoverConfig.hoverShouldShow ? this.props.hoverNav(false) : null;
    
    handleScroll = () => {    
        this.setState({ visible: document.querySelector(`.submain`).offsetTop > window.pageYOffset });
    };

    changeHeaderHover = () => {
        this.setState({ headerHover: !this.state.headerHover });
        if(this.state.headerHover) this.props.hoverNav(false);
    };

    changeHoverTrue = () => {
        if(this.props.hoverConfig.hoverShouldShow) this.props.hoverNav(true);
    };

    render() {
        return (
                <div className={this.props.hoverConfig.changeStyle ? 'hedarkStyle' : 'header'} 
                    onMouseEnter={this.changeHeaderHover} 
                    onMouseLeave={this.changeHeaderHover}
                    style={this.state.visible ? {transform: 'translateY(0%)'} : {transform: 'translateY(-100%)'}}
                >
                    <span className="iconify" data-icon="whh:doghouse" data-inline="false"></span>
                    <div className="header-links">
                        <Link to="/" onMouseEnter={this.changeHoverTrue}>Home</Link>
                        <Link to="/map" onMouseEnter={this.changeHoverTrue}>About us</Link>
                        <Link to="/" onMouseEnter={this.changeHoverTrue}>Offer</Link>
                        <Link to="/" onMouseEnter={this.changeHoverTrue}>Contact</Link>
                        <div className="header-links_details">
                            <Link to="/" onMouseEnter={() => this.props.hoverNav(false)}>
                                <ion-icon name="cart"></ion-icon>
                            </Link>
                            <Link to="/" onMouseEnter={() => this.props.hoverNav(false)}>
                                <ion-icon name="person"></ion-icon>
                            </Link>
                        </div>
                    </div>
                    <div className={this.props.hover ? "header-selected" : "header-hidden"}>
                        <div className="header-selected_title">
                            <div style={{ marginRight: '13rem' }}>Contact</div>
                        </div>
                    </div>
                </div>
        );
    };
};

const mapStateToProps = state => {
    console.log(state);
    return { hover: state.headerState, hoverConfig: state.hoverConfig };
}

export default connect(mapStateToProps, { hoverNav })(NavBar);
