import React from 'react';
import { connect } from 'react-redux';
import { fetchSearch, pageNumber, deleteFetchAll} from '../../actions';
import DisplayList from './DisplayList';
import DisplayDetail from './DisplayDetail';

class EatingSection extends React.Component {
    state = { inputValue: '', displayIntro: true };

    onBtnClick = () => {
        if(this.state.inputValue !== '') {
            this.props.clearData();
            this.props.pageNumber(0);
            this.props.findList(this.state.inputValue);
            this.setState({ inputValue: '' });
        }
    }

    renderDisplay = () => {
        if(this.state.displayIntro) return (
            <div class="">
                <br/>
                We serv you only the best food prepared
            </div>
        )
        else return (
            <div>
                
            </div>
        )
    }

    render() {
        return (
            <section className="meals">
                {this.renderDisplay()}
            </section>
        );
    }
};

const mapStateToProps = state => {
    console.log(state);
    return {weGorAPI: state.listRequest};
}

export default connect(mapStateToProps, {
    findList: fetchSearch, clearData: deleteFetchAll, pageNumber
})(EatingSection);
