import React from 'react';
import { connect } from 'react-redux';
import { navChangeStyle, headerAccepted, enterAboutPage, leavePage, hoverNav, 
    arrivalCalendarDisplay } from '../actions';
import Calendar from './calendar';

class OfferReservationPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = { startPage: false, departureCalendar: false };
        window.scrollTo(0, 0);
        this.props.navChangeStyle(true);
        this.props.headerAccepted(false);
        this.props.hoverNav(false);
        //setTimeout(() => {
        //    this.props.enterAboutPage();
        //}, 100);
        setTimeout(() => {
            this.setState({ startPage: true });
        }, 300);

        this.setWrapperRef = this.setWrapperRef.bind(this);
        this.handleClickOutside = this.handleClickOutside.bind(this);
    };

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
      }
    
      componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
      }
    
      /**
       * Set the wrapper ref
       */
      setWrapperRef(node) {
        this.wrapperRef = node;
      }
    
      /**
       * Alert if clicked on outside of element
       */
      handleClickOutside(event) {
          console.log(this.wrapperRef)
        if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
          alert('You clicked outside of me!');
        }
      }
    

    render() {
        console.log(this.props.arrivalCalendar);
        return (
            <section style={this.state.startPage ? {opacity: '1', transition: 'opacity .1s'} : {opacity: '0'}}>
               <div className="reservation-header">
                    <div className="reservation-header-data">
                        <div className="reservation-header-data_arrival">
                            
                            <Calendar ref={this.setWrapperRef}
                                style={{ position: 'absolute', bottom: '25.3rem', left: '2.35rem', zIndex: '2' }}
                            />
                            <div className="reservation-header-data-field" 
                                onClick={() => this.props.arrivalCalendarDisplay(true)}
                            />
                            <div className="reservation-header-data-title">
                                Date of arrival:
                            </div>
                            <input className="reservation-header-data-input" value='g' disabled/>
                        </div>
                        <div className="reservation-header-data_departure">
                            <div className="reservation-header-data-field"/>
                            <div className="reservation-header-data-title">
                                Date of departure:
                            </div>
                            <input className="reservation-header-data-input"value='g' disabled/>
                        </div>
                        <div className="reservation-header-data_people">
                            <div className="reservation-header-data-title_small">
                                Number of people:
                            </div>
                            <input className="reservation-header-data-input_small" type="number"></input>
                        </div>
                        <div className="reservation-header-data_enter">
                            <button className="reservation-header-data_enter-btn"></button>
                        </div>
                    </div>
               </div>
               <div className="resevation-main">
                    <div className="resevaion-main-description">
                        Choose date and amount of accompanying persons if you want to make a reservation.
                    </div>
               </div>
            </section>
        );
    };
};

const mapStateToProp = state => {
    return { arrivalCalendar: state.displayCalendars.arrivalCalendar };
}

export default connect(mapStateToProp, {
    navChangeStyle, headerAccepted, enterAboutPage, leavePage, hoverNav, arrivalCalendarDisplay
})(OfferReservationPage);
