import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { hoverNav } from './actions';

class NavBar extends React.Component {
    state = { headerHover: false, visible: true };

    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
    };
    
    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
    };

    componentDidUpdate = () => this.props.hoverNav(false);
    
    handleScroll = () => {    
        if(this.props.mainPage)
            this.setState({ visible: document.querySelector(`.submain`).offsetTop > window.pageYOffset });
    };

    changeHeaderHover = () => {
        this.setState({ headerHover: !this.state.headerHover });
        if(this.state.headerHover) this.props.hoverNav(false);
    };

    changeHoverTrue = () => this.props.hoverNav(true);

    render() {
        return (
                <div className={this.props.hoverStyle ? 'header-dark_style' : 'header'} 
                    onMouseEnter={this.changeHeaderHover} 
                    onMouseLeave={this.changeHeaderHover}
                    style={this.state.visible ? {transform: 'translateY(0%)'} : {transform: 'translateY(-100%)'}}
                >
                    <span className="iconify" data-icon="whh:doghouse" data-inline="false"></span>
                    <div className="header-links">
                        {this.props.mainPage ? 
                            null : <Link to="/" onMouseEnter={this.changeHoverTrue}>Home</Link>
                        }
                        <Link to="/about" onMouseEnter={this.changeHoverTrue}>About us</Link>
                        <Link to="/" onMouseEnter={this.changeHoverTrue}>Our offer</Link>
                        <Link to="/" onMouseEnter={this.changeHoverTrue}>Contact</Link>
                        <div className="header-links_details">
                            <Link to="/" onMouseEnter={() => this.props.hoverNav(false)}>
                                <ion-icon name="cart"></ion-icon>
                            </Link>
                            <Link to="/" onMouseEnter={() => this.props.hoverNav(false)}>
                                <ion-icon name="person"></ion-icon>
                            </Link>
                        </div>
                    </div>
                    <div className={this.props.hover ? "header-selected" : "header-hidden"}>
                        <div className="header-selected_title">
                            <div className="header-selected_title-item">
                                <Link to="/about">About us</Link>
                                <div className="header-selected_title-item-el">
                                    <Link to="/map">Location</Link>
                                </div>
                                <div className="header-selected_title-item-el">
                                    <Link to="/">Staff</Link>
                                </div>
                            </div>
                            <div className="header-selected_title-item">
                                <Link to="/">Our offer</Link>
                                <div className="header-selected_title-item-el">
                                    <Link to="/">Reservation</Link>
                                </div>
                                <div className="header-selected_title-item-el">
                                    <Link to="/">Houses</Link>
                                </div>
                                <div className="header-selected_title-item-el">
                                    <Link to="/">Rooms</Link>
                                </div>
                            </div>
                            <div className="header-selected_title-item">
                                <Link to="/">Contact</Link>
                                <div className="header-selected_title-item-el">
                                    <Link to="/">For guest</Link>
                                </div>
                                <div className="header-selected_title-item-el">
                                    <Link to="/">Jobs</Link>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
        );
    };
};

const mapStateToProps = state => {
    console.log(state);
    return { hover: state.headerState, hoverStyle: state.hoverConfig, mainPage: state.isMainPage };
}

export default connect(mapStateToProps, { hoverNav })(NavBar);
