import React from 'react';
import { Field, reduxForm } from 'redux-form';
import { connect } from 'react-redux';
import { isEmpty } from 'lodash';
import { mealsIncludeReset } from './actions';

class ContactForm extends React.Component {
    state = { isChecked: true };

    renderError = meta => meta.touched && meta.error && !meta.active ? meta.error : null;

    renderElement = formProps => {
        const { meta } = formProps;
        if (formProps.input.name === 'email')
            return (
                <div className="form-date_item">
                    Your email:<input type="email" placeholder={this.renderError(meta)} 
                    className={meta.touched && meta.error && !meta.active ? 'red-background' : ''}  
                    {...formProps.input} />
                </div>
            );
        else if(formProps.input.name === 'country') 
            return (
                <div className="form-date_item">
                    Your country of residence:<input type="text" placeholder={this.renderError(meta)} 
                    className={meta.touched && meta.error && !meta.active ? 'red-background' : ''}  
                    {...formProps.input} />
                </div>
            );
        else if(formProps.input.name === 'phoneNumber') 
            return (
                <div className="form-date_item">
                    Your phone number:<input type="number" placeholder={this.renderError(meta)} 
                    className={meta.touched && meta.error && !meta.active ? 'red-background' : ''}  
                    {...formProps.input} />
                </div>
            );
        else if(formProps.input.name === 'message') 
            return (
                <div className="form-message">
                    Your message:<input type="text" placeholder={this.renderError(meta)} 
                    className={meta.touched && meta.error && !meta.active ? 'red-background' : ''}  
                    {...formProps.input} />
                </div>
            );
        else 
            return (
                <div className="form-date_item">
                    {formProps.label}<input type="text" placeholder={this.renderError(meta)}
                    className={meta.touched && meta.error && !meta.active ? 'red-background' : ''}
                    {...formProps.input} />
                </div>
            );
    };

    onSubmitBtn = formValues => {
        console.log(formValues);
        //this.props.createNewAccount({...formValues, id});
        //this.setState({ newAccountSuccessful: true });
    };
    
    render() {
        return(
            <section className="feedback"> 
                <div className="feedback_intro">
                    Doeasomething interested you?
                </div>
                <form className="feedback_form" onSubmit={this.props.handleSubmit(this.onSubmitBtn)}>
                    <div className="form-date">
                        <Field name="firstName" component={this.renderElement} label="Your first name:" />
                        <Field name="lastName" component={this.renderElement} label="Your last name:" />
                        <div className="form-date_item">Your gender: 
                            <div className="form-date_item-gender">
                                <Field name="gender" component="input" type="radio" value="male" 
                                checked={this.state.isChecked}  onClick={() => this.setState({ isChecked: true })} />Male
                                <Field name="gender" component="input" type="radio" value="female" 
                                checked={!this.state.isChecked} onClick={() => this.setState({ isChecked: false })} />Female
                            </div>
                        </div>
                        <Field name="email" component={this.renderElement} />
                        <Field name="country" component={this.renderElement} />
                        <Field name="phoneNumber" component={this.renderElement} />
                    </div>
                    <Field name="message" component={this.renderElement} />
                </form>
            </section>
        );
    };
};

//<button className="form-btn">Create</button>
const validate = formValues => {
    const error = {};

    if(!formValues.firstName) error.firstName = 'Please, input your first name!';
    if(!formValues.lastName) error.lastName = 'Please, input your last name!';
    if(!formValues.email) error.email = 'Please, input your email!';
    if(!formValues.country) error.country = 'Please, input your country!';
    if(!formValues.phoneNumber) error.phoneNumber = 'Please, input your phone number!';
    else {
        if(formValues.phoneNumber !== 9) error.phoneNumber = 'Phone number should have 9 digits!';
    }
    if(!formValues.message) error.message = 'Please, input your password!';

    return error;
}

const mapStateToProps = state => {
    return { userAcc: state.hoverConfig };
};

const formWrapped = reduxForm({
    form: 'ContactUs', validate
})(ContactForm);

export default connect(mapStateToProps, { mealsIncludeReset })(formWrapped);
