import React from 'react';
import { connect } from 'react-redux';
import { Map, GoogleApiWrapper, InfoWindow, Marker } from 'google-maps-react';
import { headerAccepted } from '../actions';

class LocationMapPage extends React.Component {
    constructor(props) {
        super(props);
        this.props.headerAccepted(false);
        this.state = {
            showingInfoWindow: false, 
            activeMarker: {},      
            selectedPlace: {}      
          };
    };

    onMarkerClick = (props, marker) => {
        console.log
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    })};

    onClose = () => {
        if (this.state.showingInfoWindow) {
          this.setState({
            showingInfoWindow: false,
            activeMarker: null
          });
        }
    };

    render() {
        return (
            <Map
                google={this.props.google}
                zoom={14}
                style={mapStyles}
                initialCenter={{
                    lat: 49.268850,
                    lng: 19.981794
                }}
            >
            <Marker
                onClick={this.onMarkerClick}
                name={'Kenyatta International Convention Centre'}
            />
            <InfoWindow
                marker={this.state.activeMarker}
                visible={this.state.showingInfoWindow}
                onClose={this.onClose}
            >
            <div>
                <h4>{this.state.selectedPlace.name}</h4>
            </div>
            </InfoWindow>
            </Map>
        );
    };
};

const mapStyles = {
  width: '100%',
  height: '100%'
};

const mapWrapped = GoogleApiWrapper({
    apiKey: 'AIzaSyBb4uRjxqUDQyXtMRC3NBBQLy3TmdBwnNw'
  })(LocationMapPage);

export default connect(null, { headerAccepted })(mapWrapped);
