import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { navChangeStyle, headerAccepted, enterOfferPage, leavePage, hoverNav } from '../actions';

class OfferMainPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = { startPage: false  };
        window.scrollTo(0, 0);
        this.props.hoverNav(false);
        this.props.headerAccepted(true);
        setTimeout(() => {
            this.props.enterOfferPage();
        }, 100);
        setTimeout(() => {
            this.setState({ startPage: true });
        }, 300);
    };

    componentWillUnmount = () => this.props.leavePage();

    render() {
        return (
                <div style={this.state.startPage ? {opacity: '1', transition: 'opacity .1s'} : {opacity: '0'}}>
                    <section className="offer">
                        <div className="offer-title">
                            We offer best rooms in be
                        </div>
                        <div className="offer-room">

                        </div>
                    </section>
                </div>
        );
    };
};

/*const mapStateToProps = state => {
    return { hover: state.headerState };
}*/

export default connect(null, { 
    headerAccepted, navChangeStyle, enterOfferPage, leavePage, hoverNav 
})(OfferMainPage);
