import React from 'react';
import { connect } from 'react-redux';
import { navChangeStyle, headerAccepted, enterStaffSubpage, leavePage, hoverNav } from '../actions';

class LocationStaffPage extends React.Component {
    constructor(props) {
        super(props);
        this.state={leftBoard: false, rightBoard: false};
        window.scrollTo(0, 0);
        this.myRef = React.createRef(); 
        this.props.navChangeStyle(true);
        this.props.headerAccepted(false);
        this.props.hoverNav(false);
        setTimeout(() => {
            this.props.enterStaffSubpage();
        }, 100);
        setTimeout(() => {
            this.setState({ startPage: true });
        }, 300);
    };

    render() {
        return (
            <section className="staff">
                <div className="staff-board">
                    <div 
                        className={`staff-board_ceo ${this.state.leftBoard ? 'hovered' : 'not_hovered'}`}
                        onMouseEnter={() => this.setState({ leftBoard: true })}
                        onMouseLeave={() => this.setState({ leftBoard: false })}
                    >
                        Pawel
                    </div>
                    <div 
                        className={`staff-board_ceo-vice ${this.state.rightBoard ? 'hovered' : 'not_hovered'}`}
                        onMouseLeave={() => this.setState({ rightBoard: true })}
                    >
                        Piotr
                    </div>
                </div>
            </section>
        );
    };
};

export default connect(null, {
    navChangeStyle, headerAccepted, enterStaffSubpage, leavePage, hoverNav
})(LocationStaffPage);
