import React from 'react';
import moment from 'moment';
import { thisExpression } from '@babel/types';

class calendar extends React.Component {
    constructor(props) {
        super(props);
        this.width = props.width || '35rem';
        this.style = props.style || {};
        this.style.width = this.width;
    }

    state = { dateContext: moment(), today: moment(), showMonthPopup: false, showYearPopup: false };

    //weekdays = moment.weekdays(); 
    weekdaysShort = moment.weekdaysShort();
    months = moment.months();

    year = () => this.state.dateContext.format("Y");

    month = () => this.state.dateContext.format("MMMM");

    daysInMonth = () => this.state.dateContext.daysInMonth();

    currentDate = () => this.state.dateContext.get("date");

    currentDay = () => this.state.dateContext.format("D");

    firstDayOfMonth = () => moment(this.state.dateContext).startOf('month').format('d');

    weekdays = () => {
        return this.weekdaysShort.map(day => {
            return <td key={day} className="week-day">{day}</td>
        });
    };

    onChangeMonth = (e, month) => {
        e.preventDefault();
        this.setState({ showMonthPopup: !this.state.showMonthPopup });
    };

    setMonth = (month) => {
        const monthNo = this.months.indexOf(month);
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set("month", monthNo);
        this.setState({ dateContext });
    };
    onSelectChange = (e, data) => {
        e.preventDefault();
        this.setMonth(data);
    };

    MonthNav = () => {
        return (
            <span className="label-month" onClick={(e) => this.onChangeMonth(e, this.month())}>
                {this.month()}
                
                <div className="month-popup">
                    {this.state.showMonthPopup && this.months.map(month => {
                        return (
                            <div key={month}>
                                    <a href="#" onClick={(e) => this.onSelectChange(e, month)}>
                                        {month}
                                    </a>
                            </div>
                        );
                    })}
                </div>
            </span>
        );
    };

    showYearEditior = () => {
        this.setState({ showYearNav: true });
    }
    setYear = (year) => {
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set
    }
    onYearChange = (e) => {
        this.setYear(e.target.value);
        this.props.onYearChange && this.props.onYearChange(e, e.target.value);
    }
    YearNav = () => {
        return (
            this.state.showYearNav ?
            <input defaultValue={this.year()} className="editor-year" 
                ref={(yearInput) => this.yearInput = yearInput}
                onKeyUp={(e) => this.onKeyUpYear(e)}
                onChange={(e) => this.onYearChange(e)}
                type="number" placeholder="year"
            />
            :
            <span className="label-year" onClick={() => this.showYearEditior()}>
                {this.year()}
            </span>
        ); 
    };

    render() {
        const blanks = [];
        for(let i = 0; i < this.firstDayOfMonth(); i++) {
            blanks.push(
                <td className="emptySlot" key={i*10}>
                    {""}
                </td>
            );
        };

        const daysInMonth = [];
        for(let d = 1; d <= this.daysInMonth(); d++) {
            const className = (d === this.currentDay() ? "day current-day" : "day");
            daysInMonth.push(
                <td key={d} className={className}>
                    <span>{d}</span>
                </td>
            );
        };

        const totalSlots = [... blanks, ...daysInMonth];
        const rows = [];
        let cells = [];
        totalSlots.forEach((row, i) => {
            if((i % 7) !== 0) {
                cells.push(row);
            } else {
                const insertRow = cells.slice();
                rows.push(insertRow);
                cells = [];
                cells.push(row);
            }
            if(i === totalSlots.length - 1) {
                const insertRow = cells.slice();
                rows.push(insertRow);
            }
        });
        console.log(rows);

        const trElems = rows.map((d, i) => {
            return <tr key={i}>{d}</tr>;
        });

        return (
            <div className="calendar-container" style={this.style}>
                <table className="calendar">
                    <thead>
                        <tr className="calendar-header">
                            <td colSpan="5">
                                {this.MonthNav()}
                                {" "}
                                {this.YearNav()}
                            </td>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            {this.weekdays()}
                        </tr>
                        {trElems}
                    </tbody>
                </table>
            </div>
        );
    };
};

export default calendar;
