import React from 'react';
import { connect } from 'react-redux';
import { navChangeStyle, headerAccepted, enterMainPage, leavePage } from '../actions';

class LocationMainPage extends React.Component {
    constructor(props) {
        super(props);
        this.props.headerAccepted(false);
        this.props.navChangeStyle(false);
        this.state = {
          showingInfoWindow: false, 
          activeMarker: {},      
          selectedPlace: {}      
        };
        setTimeout(() => {
          this.props.enterMainPage();
        }, 100);
    };

    componentWillUnmount = () => this.props.leavePage();

    render() {
        return (
                <section className="about">
                    <div className="about-title">There is 10 years of trust behind us.</div>
                    <div className="about-title">Our biggest strength are strong bonds with clients that we create.
                       <br/> Over 80% of our client come back to us in span of the next 5 years! </div>
                    <div className="about-btn">learn more</div>
                </section>
        );
    };
};

const mapStateToProps = state => {
    return { hover: state.headerState };
}

export default connect(mapStateToProps, { 
    headerAccepted, navChangeStyle, enterAboutPage, leavePage 
})(LocationMainPage);
