import React from 'react';
import { connect } from 'react-redux';
import { navChangeStyle, headerAccepted, enterAboutPage, leavePage } from '../actions';

class LocationMainPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = { startPage: false };
        window.scrollTo(0, 0);
        this.myRef = React.createRef(); 
        this.props.navChangeStyle(true);
        this.props.headerAccepted(false);
        setTimeout(() => {
            this.props.enterAboutPage();
        }, 100);
        setTimeout(() => {
            this.setState({ startPage: true });
        }, 300);
    };

    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
        this.props.leavePage();
    };

    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
    };

    handleScroll = () => {    
        if(window.pageYOffset !== 0) {
            this.props.navChangeStyle(false);
            this.props.headerAccepted(true);
        } else {
            this.props.navChangeStyle(true);
            this.props.headerAccepted(false);
        };
    };

    componentDidUpdate = () => {
        if(window.pageYOffset !== 0) {
            this.props.navChangeStyle(false);
            this.props.headerAccepted(true);
        } else {
            this.props.navChangeStyle(true);
            this.props.headerAccepted(false);
        };
    };

    render() {
        return (
                <div style={this.state.startPage ? {opacity: '1', transition: 'opacity .2s'} : {opacity: '0'}}>
                    <section className="about">
                        <div className="about-title">There is 10 years of trust behind us.</div>
                        <div className="about-description">
                            Our biggest strength are strong bonds with clients that we create.
                            Over 80% of our client come back to us in span of the next 5 years! </div>
                        <div className="about-expand">
                            <div className="about-expand_btn">learn more</div>
                            <ion-icon name="arrow-dropdown"></ion-icon>
                        </div>
                    </section>
                    <section className="about-show">
                        <div className="about-show_line"></div>
                        <div className="about-show_info">
                            <div className="about-show_info-title">About us</div>
                            <div className="about-show_info-description">
                                Lorem ipsum dolor sit amet, consectetuer adipiscing elit. 
                                Aenean commodo ligula eget dolor. Aenean massa. Cum sociis 
                                natoque penatibus et magnis dis parturient montes, nascetur 
                                ridiculus mus. 
                                
                            </div>
                            <div className="about-show_info-arrow_left"></div>
                        </div>
                        <div className="about-show_info-quote">
                            <blockquote>
                                <p>Li Europan lingues es membres del sam familie. Lor separat existentie es un myth.</p>
                            </blockquote>
                            <div className="about-show_info-quote-arrow_left"></div>
                        </div>
                        <div className="about-show_details-quote">ddd</div>
                        <div className="about-show_details">
                            <div className="about-show_details-title">
                                Because every client is our most important client
                            </div>
                            <div className="about-show_details-description">
                                Lorem ipsum dolor sit amet, consectetuer adipiscing elit. 
                                Aenean commodo ligula eget dolor. Aenean massa. Cum sociis 
                                natoque penatibus et magnis dis parturient montes, nascetur 
                                ridiculus mus. 
                            </div>
                            <div className="about-show_info-arrow_right"></div>
                        </div>
                        <div className="about-show_backward">ddd</div>
                        <div className="about-show_forward">ddd</div>
                    </section>
                </div>
        );
    };
};

const mapStateToProps = state => {
    return { hover: state.headerState };
}

export default connect(mapStateToProps, { 
    headerAccepted, navChangeStyle, enterAboutPage, leavePage 
})(LocationMainPage);
