import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { navChangeStyle, headerAccepted, enterOfferPage, leavePage, hoverNav } from '../actions';
import rooms from './Rooms';

class OfferMainPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = { startPage: false, leftSliderHover: false, leftSliderHidden: false,
             rightSliderHover: false, currentRoom: rooms[0].id,
            changeLeftIcon: false, changeRightIcon: false };
        window.scrollTo(0, 0);
        this.props.hoverNav(false);
        this.props.headerAccepted(true);
        setTimeout(() => {
            this.props.enterOfferPage();
        }, 100);
        setTimeout(() => {
            this.setState({ startPage: true });
        }, 300);
    };

    componentWillUnmount = () => this.props.leavePage();

    currentRoomAdd = () => {
        if(this.state.currentRoom < 4) {
            const nextValue = this.state.currentRoom + 1;
            this.setState({ currentRoom: nextValue, changeLeftIcon: false });
            if(this.state.currentRoom === 3) this.setState({ changeRightIcon: true });
        };
    };

    currentRoomSubtract = () => {
        if(this.state.currentRoom > 0) {
            const nextValue = this.state.currentRoom - 1;
            this.setState({ currentRoom: nextValue, changeRightIcon: false });
        } else this.setState({ changeLeftIcon: true });
    };
        

    render() {
        console.log(this.state.changeRightIcon);
        return (
                <div style={this.state.startPage ? {opacity: '1', transition: 'opacity .1s'} : {opacity: '0'}}>
                    <section className="offer">
                        <div className="offer-title">
                            We adapt to your needs
                        </div>
                        <div className="offer-room" style={{transform: `translateX(-${this.state.currentRoom*20}%)`}}>
                            {rooms.map(rooms => {
                               return (
                                    <div className="offer-room-display" key={rooms.id}>
                                        <img className="offer-room-display-photo" src={rooms.img} alt="room"/>
                                        <div className="offer-room-display-meters">100 m2 available space</div>
                                        <div className="offer-room-display-persons">2</div>
                                        <div className="offer-room-display-details">See more</div>
                                    </div>
                               );
                            })};
                        </div>
                        <div className={`offer-slider-left ${this.state.leftSliderHover ? 'slider-left-active' : ''}`}
                            onMouseEnter={() => this.setState({ leftSliderHover: true })}
                            onMouseLeave={() => this.setState({ leftSliderHover: false })}
                            onClick={this.currentRoomSubtract}
                        >
                            <span className="iconify" data-icon="ant-design:left-outline" data-inline="false"></span>
                        </div>
                        <div className={`offer-slider-right ${this.state.rightSliderHover ? 'slider-right-active' : ''}`}
                            onMouseEnter={() => this.setState({ rightSliderHover: true })}
                            onMouseLeave={() => this.setState({ rightSliderHover: false })}
                            onClick={this.currentRoomAdd}
                        >
                            <span className="iconify" data-inline="false" data-icon="ant-design:right-outline"></span>
                        </div>
                    </section>
                </div>
        );
    };
};

/*const mapStateToProps = state => {
    return { hover: state.headerState };
}*/

export default connect(null, { 
    headerAccepted, navChangeStyle, enterOfferPage, leavePage, hoverNav 
})(OfferMainPage);
