const STATE_INITIAL = {
    hoverShouldShow: true,
    changeStyle: false
};

export default (state = false, action) => {
    switch(action.type) {
        case 'CHANGE_NAV_STYLE':
            return action.pa;
        default:
            return state;
    };
};
