import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { hoverNav } from './actions';

class NavBar extends React.Component {
    state = { headerHover: false, scrollPosition: window.pageYOffset };



    changeHeaderHover = () => {
        this.setState({ headerHover: !this.state.headerHover });
        if(this.state.headerHover) this.props.hoverNav(false);
    };

    render() {
        console.log(t);
        return (
                <div className="header" onMouseEnter={this.changeHeaderHover} 
                onMouseLeave={this.changeHeaderHover}>
                    <span className="iconify" data-icon="whh:doghouse" data-inline="false"></span>
                    <div className="header-links">
                        <Link to="/" onMouseEnter={() => this.props.hoverNav(true)}>Location</Link>
                        <Link to="/">Offer</Link>
                        <Link to="/">Contact</Link>
                        <div className="header-links_details">
                            <Link to="/"><ion-icon name="cart"></ion-icon></Link>
                            <Link to="/"><ion-icon name="person"></ion-icon></Link>
                        </div>
                    </div>
                    <div className={this.props.hover ? "header-selected" : "header-hidden"}>
                        <div className="header-selected_title">
                            Location
                        </div>
                    </div>
                </div>
        );
    };
};

const mapStateToProps = state => {
    return { hover: state.headerState };
}

export default connect(mapStateToProps, { hoverNav })(NavBar);
